import React, { useState } from "react";
import { Link } from "react-router-dom";

import styled from "styled-components";


const SiderbarLink = styled(Link)`
display: flex;
color: #e1e9fc;
justify-content: space-between;
align-items: center;
padding: 20px;
list-style: none;
height: 60px;
text-decoration:none;
font-size:18px;

&:hover{
    background:#252831;
    border-left: 4px solid #632ce4;
    cursor:pointer;
}
`;

const SiderbarLabel=styled.span`
margin-left:16px;
`;

const DropdownLink = styled(Link)`
background:#414757;
height:60px;
padding-left: 3rem;
display:flex;
align-items: center;
text-decoration: none;
color: #f5f5f5;
font-size:18px;

&:hover{
    background:'blue';
    cursor:pointer;
}
`;

const SubMenu =({item})=>{

    const[subnav, setSubnav]= useState(false)
    const showSubnav=()=>setSubnav(!subnav)

return(
        <div>
            <SiderbarLink to={item.path} onClick={ showSubnav}>
               <div>
                   {item.icon}
                   <SiderbarLabel>{item.title}</SiderbarLabel>
               </div> 
               <div>
                   {item.subNav && subnav 
                   ? item.iconOpened 
                   : item.subNav 
                   ? item.iconClosed 
                   : ''}
               </div>
            </SiderbarLink>
            {subnav && item.subNav.map((item, index)=>{
                return(
                    <DropdownLink to={item.path} key={index}>
                    {item.icon}
                    <SiderbarLabel>{item.title}</SiderbarLabel>
                     </DropdownLink>
                    );
            })}
        </div>
    );

};
export default SubMenu
