import React,{useRef} from 'react';
import axios from 'axios'

import '../assets/css/Sesion.css';
import fondoLogin from "../assets/img/fondoLogin.jpg"
import LogoVisual from "../assets/img/logo.png"

import { Fragment } from 'react/cjs/react.production.min';
import {Link} from 'react-router-dom'

const validarUsuario = async()=>
{
    let usuario =JSON.parse(sessionStorage.getItem('usuarioActivo'));
    let rol= usuario.map(usuario => <div>{usuario.Lider_proyecto_idLider_proyecto}</div>)

    console.log(rol)
    if (usuario.props==1)
    {
       
        alert("es coordinador")
    }
    else 
    {
        alert("es lider")
    }
}

const enviarDatos = async (usuario,contrasena)=>
 {
        try
        {
            var formData = new FormData();
            formData.append('usuario', usuario);
            formData.append('contrasena',contrasena);
            const res = await axios.post('http://localhost/Apis/login.php',formData).then((resJson)=>{
                return resJson.data;
            }); 
            return res;   
        }
        catch(error)
        {
            console.error(error);
        }       
}



export default function Sesion (props)
{
    const reftUsuario = useRef(null);
    const reftPassword = useRef(null);

    const handleLogin= async()=>{

           let usuario = reftUsuario.current.value;
           let contrasena = reftPassword.current.value;

           if(usuario=="" || contrasena =="")
           {
            alert("Ingrese los campos correctamente")
           }
           else
           {
             const resJson= await enviarDatos(usuario,contrasena); 
            if(resJson.conectado == false)
            {
                alert("Usuario o contrase√±a incorrectas")
            }
            else
            {
                //props.acceder(resJson.conectado)
                var res=resJson.datos
                var usuari=sessionStorage.setItem('usuarioActivo', JSON.stringify(res));
               
               //var rol= await res.Lider_proyecto_idLider_proyecto
              
               validarUsuario()
               // window.location.href="/Menu";   
               
               
  
            }
        }
    }



    return(
      <Fragment>
       <div className="">
            <img src={fondoLogin} className="fondoLogin"/>
       </div>

       <div className="container-fluid">
      <div className="row align-items-center justify-content-center center-block minh-100 ">
       <div className="col-xs-6 col-sm-6  col-md-6 col-lg-4 ">
     
            <div className="card text-white bg-secondary">
                <div className="card-header text-center ">
                     <img src={LogoVisual} className="logo img-fluid mx-auto d-block"/>
                     <h2 className="card-title">Ingresar a Suite de direcci√≥n de proyectos</h2>  
                </div>

            <div className="card-body">
                <div className="input-group mb-3">
                   <div className="input-group-prepend">
                     <span className="input-group-text" id="basic-addon1">
                     ‚úâÔ∏è</span>
                   </div>   
                <input
                 type="email"
                 className="form-control"
                 placeholder="Correo"
                 aria-label="Username"
                 aria-describedby="basic-addon1"
                 ref={reftUsuario} />
                </div>

                <div className="input-group mb-3">
                    <div className="input-group-prepend">
                    <span className="input-group-text" id="basic-addon2">
                        üîë</span>
                </div>
                
                <input 
                type="password" 
                className="form-control" 
                placeholder="Contrase√±a" 
                aria-label="Contrase√±a" 
                aria-describedby="basic-addon2" 
                ref={reftPassword}/>
                </div>

                <div className="text-center">
                <button onClick={handleLogin} 
                                className=" btn btn-lg btn-info btn-block">
                                    Acceder
                </button>
                </div>
                </div>
            </div>

            </div>
          
        </div>
     </div>
      </Fragment>     
    )
}
