     console.log(json.data);

}*/


export default function Sesion (props){


    const reftUsuario = useRef(null);
    const reftPassword = useRef(null);

    const handleLogin= async()=>{

           let usuario = reftUsuario.current.value;
           let contrasena = reftPassword.current.value;
       
      // enviarData(urlLogin,data);
         const resp= await enviarDatos(usuario,contrasena);
         console.log(resp.conectado);

         props.acceder(resp);

    }

    return(

        <div className="Sesion">

        <div className="row">
            <div className="col-sm-4 offset-4 mt-5">

            <div className="card pt-5">
               <div className="card-header">
                    iniciar
                </div>
                <div className="card-body">

                <div className="input-group mb-3">
                 <span className="input-group-text" id="basic-addon1">九괦잺</span>
                <input
                 type="email"
                 className="form-control"
                 placeholder="Correo"
                 aria-label="Username"
                 aria-describedby="basic-addon1"
                 ref={reftUsuario} />
                </div>

                <div className="input-group mb-3">
                 <span className="input-group-text" id="basic-addon2">游댐</span>
                <input 
                type="password" 
                className="form-control" 
                placeholder="Contrase침a" 
                aria-label="Contrase침a" 
                aria-describedby="basic-addon2" 
                ref={reftPassword}/>
                </div>

                <button onClick={handleLogin} className="btn btn-info btn-lg btn-block">
                    Acceder
                </button>

                <div className="card-footer">
                    <span>쯆lvid칩 su contrase침a?</span>
                </div>

                </div>
            </div>

            </div>
        </div>

        </div>
    )
}
