import React,{useRef} from 'react';
import axios from 'axios'

import '../assets/css/Sesion.css';
 

const enviarDatos = async (usuario,contrasena)=>
 {
        try
        {
            var formData = new FormData();
            formData.append('usuario', usuario);
            formData.append('contrasena',contrasena);
            const res = await axios.post('http://localhost/Apis/login.php',formData);
            const json = await res.data.datos;

            if(json==0)
            {
                alert("usuario o contraseña incorrecta");
              
            }
            else{
             
                console.log(json);
                return json;
            }
            
        }catch(error)
        {
            console.error(error);
        }
            
}


export default function Sesion (props){


    const reftUsuario = useRef(null);
    const reftPassword = useRef(null);

    const handleLogin= async()=>{

           let usuario = reftUsuario.current.value;
           let contrasena = reftPassword.current.value;

           if(usuario=="" || contrasena =="")
           {
               alert("Ingrese los campos correctamente")
           }
           else
           {
              const resJson=enviarDatos(usuario,contrasena);
              console.log("respuesta desde el evento: ", resJson.conectado);
  
              props.acceder(resJson.conectado);
           }

    }

    return(

        <div className="Sesion">

        <div className="row">
            <div className="col-sm-4 offset-4 mt-5">

            <div className="card pt-5">
               <div className="card-header">
                    iniciar
                </div>
                <div className="card-body">

                <div className="input-group mb-3">
                 <span className="input-group-text" id="basic-addon1">✉️</span>
                <input
                 type="email"
                 className="form-control"
                 placeholder="Correo"
                 aria-label="Username"
                 aria-describedby="basic-addon1"
                 ref={reftUsuario} />
                </div>

                <div className="input-group mb-3">
                 <span className="input-group-text" id="basic-addon2">🔑</span>
                <input 
                type="password" 
                className="form-control" 
                placeholder="Contraseña" 
                aria-label="Contraseña" 
                aria-describedby="basic-addon2" 
                ref={reftPassword}/>
                </div>

                <button onClick={handleLogin} className="btn btn-info btn-lg btn-block">
                    Acceder
                </button>

                <div className="card-footer">
                    <span>¿Olvidó su contraseña?</span>
                </div>

                </div>
            </div>

            </div>
        </div>

        </div>
    )
}
